            <script>
      document.addEventListener("DOMContentLoaded", () => {
        const container = document.getElementById("cardContainer");
        const btnLeft = document.getElementById("btnLeft");
        const btnRight = document.getElementById("btnRight");

        if (!container) {
          console.error("cardContainer not found");
          return;
        }

        // Calculate scroll step as card width + gap
        function getCardStep() {
          const firstCard = container.querySelector(".card");
          if (!firstCard) return 220; // fallback
          const style = getComputedStyle(firstCard);
          const marginLeft = parseInt(style.marginLeft) || 0;
          const marginRight = parseInt(style.marginRight) || 0;
          return firstCard.offsetWidth + marginLeft + marginRight + 668; // +16 = gap
        }

        function updateButtons() {
          btnLeft.disabled = container.scrollLeft <= 0;
          btnRight.disabled =
            Math.ceil(container.scrollLeft + container.clientWidth) >=
            container.scrollWidth;
        }

        btnLeft.addEventListener("click", () => {
          const step = getCardStep();
          container.scrollBy({ left: -step, behavior: "smooth" });
        });

        btnRight.addEventListener("click", () => {
          const step = getCardStep();
          container.scrollBy({ left: step, behavior: "smooth" });
        });

        container.addEventListener("scroll", updateButtons);
        window.addEventListener("resize", updateButtons);

        // initial state
        updateButtons();
      });



      
    //  js for start loader
            const loadingBox = document.querySelector(".loader-container");
            const minTime = 2000; 
            const transitionTime = 500; 
            const pageReadyTime = Date.now();

            function hideLoader() {
                const elapsedTime = Date.now() - pageReadyTime;
                const delay = elapsedTime < minTime ? minTime - elapsedTime : 0;

                setTimeout(() => {
                    loadingBox.classList.add("hidden");
                    setTimeout(() => {
                        loadingBox.remove();
                    }, transitionTime); 
                }, delay);
            }

            window.addEventListener("load", hideLoader());

      
    </script>
   

